SELECT COUNT(*) AS ppe_non_compliance_unsafe_events
FROM unsafe_events_srs
WHERE unsafe_act ILIKE '%PPE non compliance%' OR unsafe_act ILIKE '%Lack or improper use of PPE%' OR unsafe_act ILIKE '%Lack or improper use of PPE,%' OR unsafe_act ILIKE '%Lack or improper use of PPE,%';

SELECT COUNT(*) AS count_unsafe_events_ppe_non_compliance
FROM unsafe_events_srs_enriched
WHERE unsafe_act ILIKE '%Lack or improper use of PPE%';

