SELECT COUNT(*) AS count_unsafe_events_before_heavy_vehicle_entry
FROM unsafe_events_srs_enriched
WHERE (unsafe_act ILIKE '%heavy vehicle entry%' OR comments/remarks ILIKE '%heavy vehicle entry%' OR unsafe_act_other ILIKE '%heavy vehicle entry%' OR other_safety_issues ILIKE '%heavy vehicle entry%' OR action_description_1 ILIKE '%heavy vehicle entry%');

SELECT 
  COUNT(*) FILTER (WHERE work_stopped = 'Yes')::float / NULLIF(COUNT(*),0) AS work_stoppage_rate
FROM unsafe_events_srs_enriched;

SELECT COUNT(*) AS safety_incident_breach_count
FROM unsafe_events_srs_enriched
WHERE reported_date = '2025-03-01';
